bootstrap.rules:
.card .card-attribution {
  color: $brand-blue;
}
.about-image {
  border: 2px solid $brand-forest-green;
}
.card {
  border-color: lighten($brand-dark-grey, 15%);
}
/* Target only listing title links, not description text */
.quarto-listing .listing-item .listing-title a,
.quarto-listing .listing-item h3 a,
.quarto-listing .listing-item h4 a,
.quarto-listing .listing-item h2 a {
  color: $brand-blue !important;
  text-decoration: none;
}
.quarto-listing .listing-item .listing-title a:hover,
.quarto-listing .listing-item h3 a:hover,
.quarto-listing .listing-item h4 a:hover,
.quarto-listing .listing-item h2 a:hover {
  color: lighten($brand-blue, 15%) !important;
  text-decoration: underline;
}

